import { Paper } from "@material-ui/core";
import React, { useState, useEffect } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
} from "recharts";

export default function LineGraph({ data }) {
  const [processedData, setProcessedData] = useState(null);
  useEffect(() => {
    if (Object.keys(data).length > 0) {
      // console.log(data);
      const keys = Object.keys(data["cases"]);
      const cases = Object.values(data["cases"]).sort();
      const deaths = Object.values(data["deaths"]).sort();
      const recovered = Object.values(data["recovered"]).sort();
      let newData = [];
      let obj = {};
      for (let index = 0; index < keys.length; index++) {
        if (index === 0) {
          obj = {
            "Total Confirmed": cases[index],
            "Total Deaths": deaths[index],
            "Total Recovered": recovered[index],
          };
          // TotalData.push(obj)
        } else {
          obj = {
            name: keys[index],
            "Total Confirmed": cases[index],
            "Total Deaths": deaths[index],
            "Total Recovered": recovered[index],
          };
          newData.push(obj);
        }
      }
      // const newData = data.map(mapper);
      setProcessedData(newData);
    }
  }, [data]);
  return (
    <Paper
      elevation={2}
      style={{ margin: "auto", marginTop: "2%", width: "90%" }}
    >
      <LineChart
        style={{ margin: "auto" }}
        width={700}
        height={700}
        data={processedData}
        margin={{
          top: 5,
          right: 30,
          left: 20,
          bottom: 5,
        }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="name" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Line dataKey="Total Confirmed" stroke="#4099ff" activeDot={{ r: 8 }} />
        <Line dataKey="Total Deaths" stroke="#fc032d" />
        <Line dataKey="Total Recovered" stroke="#00C49F" />
      </LineChart>
    </Paper>
  );
}
