import {
  AppBar,
  Button,
  Dialog,
  DialogContent,
  DialogTitle,
  MenuItem,
  Select,
  TextField,
  Toolbar,
  Typography,
} from "@material-ui/core";
import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";

// Firebase Imports
import {
  auth, 
  googleProvider, 
  db
} from "../base"

export default function Appbar() {

  // State hooks
  const [user, setUser] = useState(null);
  const [dialog, setDialog] = useState(false);
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState(null);
  const [description, setDescription] = useState(null);
  // const [date, setDate] = useState(null);

  // Google Sign-in Functionality 
  const signInWithGoogle = () => {
    auth
      .signInWithPopup(googleProvider)
      .then((res) => {
        var userRef = db.collection("users").doc(res.user.email);
        userRef.get().then(async (doc) => {
          if (doc.exists) {
            setUser(res.user);
          } else {
            // auth.signOut();
            setUser(null);
          }
        });
        console.log(res.user);
      })
      .catch((error) => {
        console.log(error.message);
      });
  };

  // useEffect(() => {
  //   auth.onAuthStateChanged(async (user) => {
  //     const { displayName, email } = user;
  //     setUser({
  //       displayName,
  //       email
  //     })
  //   })
  // },[])

  useEffect(() => {
    auth.onAuthStateChanged(function(user) {
      if (user) {
        setUser(user);
        // console.log(user);
      } else {
        console.log("User not signed in");
      }
    });

  })


  // Set the current user on rendering the page
  // If not logged-in, this would be null 
  // useEffect(() => {
  //   setUser(auth.currentUser);
  // }, [auth.currentUser]);

  // Set country data
  useEffect( () => {

    const fetchData = async () => {

      const countryUrl = "https://api.covid19api.com/countries";

      try {
        const res = await axios.get(countryUrl);
        const countries_ = res.data.map((el) => el["Country"]).sort();
        setCountries(countries_);
      } catch (error) {
        console.log(error);
      }
    }

    fetchData();


  }, []);

  // Function to add news to the top bar
  const addNews = () => {
    console.log(description);
    console.log(selectedCountry);
    console.log(new Date().toDateString());
    console.log(auth.currentUser);
    db.collection("userNews")
      .doc()
      .set({
        description: description,
        date: new Date().toDateString(),
        user: auth.currentUser.displayName,
        country: selectedCountry,
      })
      .then(() => {
        console.log("News Added");
      })
      .catch((error) => {
        console.log("error in adding news to DB");
      });
    setDialog(false);
  };


  return (
    <Fragment>
      {/* <AppBar position="static"> */}
        {/* <Toolbar> */}
          <Typography variant="h6" style={{ flexGrow: 1 }}>
            {user ? ("Welcome " + user.displayName) : ""}
          </Typography>
          {!user && (
            <Button color="inherit" onClick={signInWithGoogle}>
              Login
            </Button>
          )}
          {user && (
            <Fragment>
              <Button color="inherit" onClick={() => setDialog(true)}>
                Add News
              </Button>
              <Dialog open={dialog} onClose={() => setDialog(false)}>
                <DialogTitle>Let's add some News</DialogTitle>
                <DialogContent>
                  Specify the news characteristics
                  <br />
                  <Select
                    placeholder={"Select which country"}
                    id="demo-simple-select"
                    value={selectedCountry}
                    fullWidth
                    style={{ marginTop: "5%" }}
                    onChange={(e) => setSelectedCountry(e.target.value)}
                  >
                    <MenuItem value={"worldwide"}>WorldWide</MenuItem>
                    {countries.map((country) => (
                      <MenuItem value={country}>{country}</MenuItem>
                    ))}
                  </Select>
                  <br />
                  <TextField
                    placeholder="News Description..."
                    rows={5}
                    multiline
                    fullWidth
                    value={description}
                    style={{ marginTop: "5%" }}
                    onChange={(e) => setDescription(e.target.value)}
                  />
                  <Button variant="contained" onClick={addNews} fullWidth>
                    SUBMIT
                  </Button>
                </DialogContent>
              </Dialog>
            </Fragment>
          )}
          {user && (
            <Button
              color="inherit"
              onClick={() => {
                auth.signOut().then(() => console.log("signed out"));
                setUser(null);
              }}
            >
              Signout
            </Button>
          )}
        {/* </Toolbar> */}
      {/* </AppBar> */}
    </Fragment>
  );
}
