// General Imports 
import React, { useState, useEffect, Fragment } from "react";
import { withRouter } from "react-router-dom";
import axios from "axios";
import {
  Card,
  CardContent,
} from "@material-ui/core";

// Import the Custom Components
import Summary from "../components/Summary";
import PieChartCovid from "../components/PieChartCovid";
import BarChartCovid from "../components/BarChartCovid";
import LineChartCovid from "../components/LineChartCovid";
import CountryTable from "../components/CountryTable";
import LoginNews from "../components/LoginNews";


//firebase Imports
import {db} from "../base"

// const db = app.firestore();

function HomePage({ history }) {
  // const [loggedIn, setLoggedIn] = useState(false);
  const [covidSummary, setCovidSummary] = useState({});
  // const [covidWorld, setCovidWorld] = useState([]);
  const [lastSevenDays, setLastSevenDays] = useState({});
  const [lastThirtyDays, setLastThirtyDays] = useState({});
  const [countryNews, setCountryNews] = useState([]);

  // Global News as a firebase collection 
  var latestNewsRef = db.collection("news").doc("global");

  // Updating the news collection
  const updateDB = (data, date, doc) => {
    db.collection("news").doc(doc).set({
        content: data,
        date: date,
      }).then(() => {
        console.log("DB updated");
      })
      .catch((error) => {
        console.log("error in updating DB");
      });
  };  

  // Run UseEffect once to get the data through the api call
  useEffect( () => {

    const fetchData = async () => {
      const summaryUrl = "https://api.covid19api.com/summary" ;
      const lastSevenDaysUrl = "https://corona.lmao.ninja/v2/historical/all?lastdays=8" ; 
      const lastThirtyDaysUrl = "https://corona.lmao.ninja/v2/historical/all" ; 

      try {
        latestNewsRef.get().then(async (doc) => {
          const today = parseInt(new Date().getTime() / 86400000);
          if (doc.exists) {
            const date = doc.data().date;
            if (today > date) {
              const resSummary = await axios.get(summaryUrl);
              setCovidSummary(resSummary.data);
              updateDB(resSummary.data, today, "global");
            } else {
              console.log("cached from DB");
              setCovidSummary(doc.data().content);
            }
          } 
          else {
            const resSummary = await axios.get(summaryUrl);
            setCovidSummary(resSummary.data);
            updateDB(resSummary.data, today, "global");
          }
        });

        const resSeven = await axios.get(lastSevenDaysUrl);
        setLastSevenDays(resSeven.data);
        const resThirty = await axios.get(lastThirtyDaysUrl);
        setLastThirtyDays(resThirty.data);

      } catch (error) {
        console.log(error);
      }
    }

    fetchData();


  }, []);

  // Handling user news
  useEffect(() => {
    db.collection("userNews")
      .where("country", "==", "worldwide")
      .get()
      .then(function (querySnapshot) {
        let newNews = [];
        querySnapshot.forEach(function (doc) {
          newNews.push(doc.data());
        });
        setCountryNews(newNews);
      })
      .catch(function (error) {
        console.log("Error getting documents: ", error);
      });
  }, []);


  return (
    <div className="home">
      <div class="container">
      <h1>Worldwide Data</h1>
      <Fragment>
        <LoginNews heading="WorldWide Data"/>
          <div class="container sm-12">
            {countryNews.length > 0 ? (
              countryNews.map((el) => (
                <div class="card align-items-center" style={{ margin: "1% " ,maxWidth:'200px', overflow:'scroll'}}>
                  <CardContent>
                    User: {el.user}
                    <br />
                    Date: {el.date}
                    <br />
                    Description: {el.description}
                  </CardContent>
                </div>
              ))
            ) : (
              <div class="card text-center" style={{ margin: "1% ", maxWidth:'200px' }}>
                <CardContent>No News!</CardContent>
              </div>
            )}
        </div>
          {Object.keys(covidSummary).includes("Global") && (
            <Fragment>
              <Summary data={covidSummary["Global"]} />
              <PieChartCovid data={covidSummary["Global"]} />
            </Fragment>
          )}
          {lastSevenDays && (
            <Fragment>
              <BarChartCovid data={lastSevenDays} />
              <LineChartCovid data={lastThirtyDays} />
            </Fragment>
          )}
          {Object.keys(covidSummary).includes("Global") && (
            // <Fragment>
              <CountryTable data={covidSummary["Countries"]} />
            // </Fragment>
          )}
        {/* </div> */}
      </Fragment>
      <br/>
      <br/>
      {/* </div> */}
    </div>
    </div>
  );
}

HomePage.propTypes = {};

export default withRouter(HomePage);
